        return chr($d + 22 + 75 * ($d < 26));
    }
    private function _decode_digit($cp)
    {
        $cp = ord($cp);
        return $cp - 48 < 10 ? $cp - 22 : ($cp - 65 < 26 ? $cp - 65 : ($cp - 97 < 26 ? $cp - 97 : $this->_base));
    }
    private function _error($error = "")
    {
        $this->_error = $error;
    }
    private function _nameprep($input)
    {
        $output = array();
        $error = false;
        foreach ($input as $v) {
            if (in_array($v, $this->NP["map_nothing"])) {
                continue;
            }
            if (in_array($v, $this->NP["prohibit"]) || in_array($v, $this->NP["general_prohibited"])) {
                $this->_error("NAMEPREP: Prohibited input U+" . sprintf("%08X", $v));
                return false;
            }
            foreach ($this->NP["prohibit_ranges"] as $range) {
                if ($range[0] <= $v && $v <= $range[1]) {
                    $this->_error("NAMEPREP: Prohibited input U+" . sprintf("%08X", $v));
                    return false;
                }
            }
            if (44032 <= $v && $v <= 55215) {
                foreach ($this->_hangul_decompose($v) as $out) {
                    $output[] = (int) $out;
                }
            } else {
                if (isset($this->NP["replacemaps"][$v])) {
                    foreach ($this->_apply_cannonical_ordering($this->NP["replacemaps"][$v]) as $out) {
                        $output[] = (int) $out;
                    }
                } else {
                    $output[] = (int) $v;
                }
            }
        }
        $output = $this->_hangul_compose($output);
        $last_class = 0;
        $last_starter = 0;
        $out_len = count($output);
        for ($i = 0; $i < $out_len; $i++) {
            $class = $this->_get_combining_class($output[$i]);
            if ((!$last_class || $class < $last_class) && $class) {
                $seq_len = $i - $last_starter;
                $out = $this->_combine(array_slice($output, $last_starter, $seq_len));
                if ($out) {
                    $output[$last_starter] = $out;
                    if (count($out) != $seq_len) {
                        for ($j = $i + 1; $j < $out_len; $j++) {
                            $output[$j - 1] = $output[$j];
                        }
                        unset($output[$out_len]);
                    }
                    $i--;
                    $out_len--;
                    $last_class = $i == $last_starter ? 0 : $this->_get_combining_class($output[$i - 1]);
                    continue;
                }
            }
            if (!$class) {
                $last_starter = $i;
            }
            $last_class = $class;
        }
        return $output;
    }
    private function _hangul_decompose($char)
    {
        $sindex = (int) $char - $this->_sbase;
        if ($sindex < 0 || $this->_scount <= $sindex) {
            return array($char);
        }
        $result = array();
        $result[] = (int) $this->_lbase + $sindex / $this->_ncount;
        $result[] = (int) $this->_vbase + $sindex % $this->_ncount / $this->_tcount;
        $T = intval($this->_tbase + $sindex % $this->_tcount);
        if ($T != $this->_tbase) {
            $result[] = $T;
        }
        return $result;
    }
    private function _hangul_compose($input)
    {
        $inp_len = count($input);
        if (!$inp_len) {
            return array();
        }
        $result = array();
        $last = (int) $input[0];
        $result[] = $last;
        for ($i = 1; $i < $inp_len; $i++) {
            $char = (int) $input[$i];
            $sindex = $last - $this->_sbase;
            $lindex = $last - $this->_lbase;
            $vindex = $char - $this->_vbase;
            $tindex = $char - $this->_tbase;
            if (0 <= $sindex && $sindex < $this->_scount && $sindex % $this->_tcount == 0 && 0 <= $tindex && $tindex <= $this->_tcount) {
                $last += $tindex;
                $result[count($result) - 1] = $last;
                continue;
            }
            if (0 <= $lindex && $lindex < $this->_lcount && 0 <= $vindex && $vindex < $this->_vcount) {
                $last = (int) $this->_sbase + ($lindex * $this->_vcount + $vindex) * $this->_tcount;
                $result[count($result) - 1] = $last;
                continue;
            }
            $last = $char;
            $result[] = $char;
        }
        return $result;
    }
    private function _get_combining_class($char)
    {
        return isset($this->NP["norm_combcls"][$char]) ? $this->NP["norm_combcls"][$char] : 0;
    }
    private function _apply_cannonical_ordering($input)
    {
        $swap = true;
        $size = count($input);
        while ($swap) {
            $swap = false;
            $last = $this->_get_combining_class(intval($input[0]));
            for ($i = 0; $i < $size - 1; $i++) {
                $next = $this->_get_combining_class(intval($input[$i + 1]));
                if ($next != 0 && $next < $last) {
                    for ($j = $i + 1; 0 < $j; $j--) {
                        if ($this->_get_combining_class(intval($input[$j - 1])) <= $next) {
                            break;
                        }
                        $t = intval($input[$j]);
                        $input[$j] = intval($input[$j - 1]);
                        $input[$j - 1] = $t;
                        $swap = true;
                    }
                    $next = $last;
                }
                $last = $next;
            }
        }
        return $input;
    }
    private function _combine($input)
    {
        $inp_len = count($input);
        foreach ($this->NP["replacemaps"] as $np_src => $np_target) {
            if ($np_target[0] != $input[0]) {
                continue;
            }
            if (count($np_target) != $inp_len) {
                continue;
            }
            $hit = false;
            foreach ($input as $k2 => $v2) {
                if ($v2 == $np_target[$k2]) {
                    $hit = true;
                } else {
                    $hit = false;
                    break;
                }
            }
            if ($hit) {
                return $np_src;
            }
        }
        return false;
    }
    private function _utf8_to_ucs4($input)
    {
        $output = array();
        $out_len = 0;
        if (function_exists("mb_strlen")) {
            $inp_len = mb_strlen($input, "8bit");
        } else {
            $inp_len = strlen($input);
        }
        $mode = "next";
        $test = "none";
        for ($k = 0; $k < $inp_len; $k++) {
            $v = ord($input[$k]);
            if ($v < 128) {
                $output[$out_len] = $v;
                $out_len++;
                if ("add" == $mode) {
                    $this->_error("Conversion from UTF-8 to UCS-4 failed: malformed input at byte " . $k);
                    return false;
                }
                continue;
            }
            if ("next" == $mode) {
                $start_byte = $v;
                $mode = "add";
                $test = "range";
                if ($v >> 5 == 6) {
                    $next_byte = 0;
                    $v = $v - 192 << 6;
                } else {
                    if ($v >> 4 == 14) {
                        $next_byte = 1;
                        $v = $v - 224 << 12;
                    } else {
                        if ($v >> 3 == 30) {
                            $next_byte = 2;
                            $v = $v - 240 << 18;
                        } else {
                            if ($v >> 2 == 62) {
                                $next_byte = 3;
                                $v = $v - 248 << 24;
                            } else {
                                if ($v >> 1 == 126) {
                                    $next_byte = 4;
                                    $v = $v - 252 << 30;
                                } else {
                                    $this->_error("This might be UTF-8, but I don't understand it at byte " . $k);
                                    return false;
                                }
                            }
                        }
                    }
                }
                if ("add" == $mode) {
                    $output[$out_len] = (int) $v;
                    $out_len++;
                    continue;
                }
            }
            if ("add" == $mode) {
                if (!$this->_allow_overlong && $test == "range") {
                    $test = "none";
                    if ($v < 160 && $start_byte == 224 || $v < 144 && $start_byte == 240 || 143 < $v && $start_byte == 244) {
