            } else {
                if ($v < 1 << 11) {
                    $output .= chr(192 + ($v >> 6)) . chr(128 + ($v & 63));
                } else {
                    if ($v < 1 << 16) {
                        $output .= chr(224 + ($v >> 12)) . chr(128 + ($v >> 6 & 63)) . chr(128 + ($v & 63));
                    } else {
                        if ($v < 1 << 21) {
                            $output .= chr(240 + ($v >> 18)) . chr(128 + ($v >> 12 & 63)) . chr(128 + ($v >> 6 & 63)) . chr(128 + ($v & 63));
