                            if (self::$safe_mode) {
                                $output .= self::$safe_char;
                            } else {
                                $this->_error("Conversion from UCS-4 to UTF-8 failed: malformed input at byte " . $k);
                                return false;
                            }
                        }
                    }
                }
            }
        }
        return $output;
    }
    private function _ucs4_to_ucs4_string($input)
    {
        $output = "";
